mapper.update:

UPDATE task_submit
SET 
fileName=#{fileName},
originalFileName=#{originalFileName},
fileSize=#{fileSize}
WHERE seq = #{seq}

mapper.select:

SELECT * FROM task_submit 
WHERE parent=#{task.seq} 
AND IF(#{user.auth}='교수' OR #{user.auth} IS NULL,writer LIKE '%%',writer=#{user.userId})


SELECT * FROM task_submit 
WHERE parent=#{task.seq} 
AND writer=#{user.userId}
